import { Component, Inject, OnInit } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { Recipe } from '../model/recipe';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiServiceService } from '../api-service.service';

@Component({
  selector: 'app-add-ing-dialog',
  templateUrl: './add-ing-dialog.component.html',
  styleUrls: ['./add-ing-dialog.component.css']
})
export class AddIngDialogComponent implements OnInit {

  constructor(@Inject(MAT_DIALOG_DATA) public data: Recipe, private dialogRef: MatDialogRef<AddIngDialogComponent>,private fb: FormBuilder, private api: ApiServiceService) { }

  ngOnInit() {
    this.initForm()
  }

  formData!: FormGroup;
  initForm(){
    this.formData = this.fb.group({
      name: ['', Validators.required],
      ingredient : [''],
      quantity: [0, Validators.required],
    })
  }

  onSubmit(){
    if (this.formData.valid) {
      this.data.ingredients.push(this.formData.value);

      this.api.updateCollection(this.data);
      alert('Changes made successfully')

    }
  }

  onCancel(){
    this.dialogRef.close([]);
  }

}
<div class="card">
    <div class="card-title">
        <h1>Add Ingredients</h1>
    </div>
    <div class="card-body">
        <form [formGroup]="formData">
            <mat-form-field class="mx-2">
                <mat-label>Ingredient</mat-label>
                <input matInput formControlName="name" required>
            </mat-form-field>
            <mat-form-field class="mx-2">
                <mat-label>Measurement/Amount/Capacity</mat-label>
                <input matInput formControlName="ingredient">
            </mat-form-field>
            <mat-form-field class="mx-2">
                <mat-label>Quantity</mat-label>
                <input matInput formControlName="quantity" required>
            </mat-form-field>
            <div class="d-flex justify-content-end">
            <button mat-raised-button color="primary" class="mr-2" (cl)="onSubmit()">Save</button>
            <button mat-raised-button color="primary" class="ml-2" (click)="onCancel()">Cancel</button>
            </div>
        </form>
    </div>
</div>
