import { HttpClient } from '@angular/common/http';
import { Component, OnInit, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router, RouterModule } from '@angular/router';
import { ApiServiceService } from '../api-service.service';

@Component({
  selector: 'app-user-login',
  templateUrl: './user-login.component.html',
  styleUrls: ['./user-login.component.css']
})
export class UserLoginComponent implements OnInit {

  @ViewChild('login', {static:false}) loginData: NgForm | any

  constructor(private http: HttpClient, private api: ApiServiceService, private router:Router) { }

  ngOnInit() {
  }

  onLogin(){
    this.http.get<any>('http://localhost:3000/users').subscribe(res=>{
      const user = res.find((a:any)=>{
        return a.email === this.loginData.value.email && a.password === this.loginData.value.password
      });

      if(user){
        alert('You are successfully logged in!');
        this.loginData.reset();
        this.router.navigate(['']);
        localStorage.setItem('userName', user.name);
        localStorage.setItem('userId', user.id.toString());
      } else{
        alert('User Not Found');
        this.router.navigate(['/login']);
      }
    }, err=>{
      alert('Something went Wrong')
    })
  }
}
<form #login="ngForm" (ngSubmit)="onLogin()">
    <div class="container py-5 h-100">
        <div class="row d-flex justify-content-center align-items-center h-100">
            <div class="col-12 col-md-8 col-lg-6 col-xl-5">
                <div class="card my-5 mx-2 text-dark" style="border-radius: 1rem;">
                    <div class="card-body px-5 py-3 text-center">

                        <div class="mb-md-5 mt-md-4 pb-5">

                            <h2 class="fw-bold mb-2 text-uppercase">Login</h2>
                            <p class="text-dark-50 mb-5">Please enter your login credentials!</p>

                            <div class="form-group form-outline form-white mb-4">
                                <input type="email" id="typeEmailX" class="form-control form-control-lg" name="email" ngModel required email #email=""/>
                                <label class="form-label" for="typeEmailX">Email</label>
                            </div>

                            <div class="form-group form-outline form-white mb-4">
                                <input type="password" id="typePasswordX" class="form-control form-control-lg" name="password" ngModel required password/>
                                <label class="form-label" for="typePasswordX">Password</label>
                            </div>

                            <button class="btn btn-lg px-5 my-4" type="submit">Login</button>

                            <p class="mb-0">Don't have an account? <a routerLink="/registration" class="fw-bold">Sign Up</a></p>

                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</form>
