import { Component, Inject, OnInit } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogModule, MatDialogRef } from '@angular/material/dialog';
import { Recipe } from '../model/recipe';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiServiceService } from '../api-service.service';

@Component({
  selector: 'app-edit-info-dialog',
  templateUrl: './edit-info-dialog.component.html',
  styleUrls: ['./edit-info-dialog.component.css']
})
export class EditInfoDialogComponent implements OnInit {

  constructor(@Inject(MAT_DIALOG_DATA) public data: Recipe, private dialogRef: MatDialogRef<EditInfoDialogComponent>,private fb: FormBuilder, private api: ApiServiceService) { }

  ngOnInit() {
    this.initForm()
  }

  nutriInfo!: FormGroup;
  initForm(){
    this.nutriInfo = this.fb.group({
      calories: ['', Validators.required],
      protein: ['', Validators.required],
      carbohydrates: ['', Validators.required],
      fat: ['', Validators.required],
    })
  }

  onSubmit(){
    if (this.nutriInfo.valid) {
      this.data.nutritional_information = this.nutriInfo.value;

      this.api.updateCollection(this.data);
    }
  }

  onCancel(){
    this.dialogRef.close([]);
  }

}
<div class="card">
    <div class="card-title">
        <h1>Edit Nutritional Information</h1>
    </div>
    <div class="card-body">
        <form [formGroup]="nutriInfo" (ngSubmit)="onSubmit()">
            <mat-form-field>
                <mat-label class="mx-2">Calories</mat-label>
                <input matInput formControlName="calories" required>
            </mat-form-field>
            <mat-form-field class="mx-2">
                <mat-label>Protein</mat-label>
                <input matInput formControlName="protein" required>
            </mat-form-field>
            <mat-form-field class="mx-2">
                <mat-label>Carbohydrate</mat-label>
                <input matInput formControlName="carbohydrates" required>
            </mat-form-field>
            <mat-form-field class="mx-2">
                <mat-label>Fat</mat-label>
                <input matInput formControlName="fat" required>
            </mat-form-field>
            <div class="d-flex justify-content-end">
            <button mat-raised-button color="primary" class="mr-2" type="submit">Save</button>
            <button mat-raised-button color="primary" class="ml-2" (click)="onCancel()">Cancel</button>
            </div>
        </form>
    </div>
</div>
